mapper.select:
SELECT * 
  FROM (SELECT A.*, 
  			   rownum AS rn 
  		  FROM (SELECT * 
  		  	 	  FROM qna_tb 
  		  	 	 WHERE qnaShow ='Y'

					AND qnaWriter = #{qnaWriter} 



						AND qnaWriter = #{qnaWriter} 


						AND qnaWriter IS NULL 


						ORDER BY qnaNo DESC) A ) WHERE rn BETWEEN #{start} AND #{end}

SELECT * FROM (SELECT A.*, rownum AS rn FROM (SELECT * FROM qna_tb
WHERE qnaStatus = #{statusType}
ORDER BY qnaNo DESC) A ) WHERE rn BETWEEN #{start} AND #{end}

SELECT COUNT(*) 
  FROM qna_tb

  WHERE qnaStatus = #{statusType}

mapper.update:

UPDATE qna_tb 
   SET qnaTitle = #{qnaTitle}, 
	   qnaContent=#{qnaContent} 
 WHERE qnaNo = #{qnaNo}


UPDATE qna_tb 
   SET qnaShow = 'N' 
 WHERE qnaNo = #{num}


UPDATE qna_tb 
   SET qnaStatus = #{qnaStatus} 
 WHERE qnaNo = #{num}


UPDATE qna_comment_tb 
   SET q_comContent = #{q_comContent} 
 WHERE qnaNo = #{qnaNo} AND q_comNo = #{q_comNo}

UPDATE qna_tb
   SET qnaShow = 'Y'
 WHERE qnaNo = #{num}
